contracts/facets/LiFiPortalSwapRouterFac.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/interfaces/IERC4626.sol";
import {UsingDiamondOwner} from "hardhat-deploy/solc_0.8/diamond/UsingDiamondOwner.sol";

import "../interfaces/ILiFiPortalSwapRouter.sol";

import {LibLiFi} from "../libraries/LibLiFi.sol";

import "hardhat/console.sol";

contract LiFiPortalSwapRouterFacet is ILiFiPortalSwapRouter, UsingDiamondOwner {
    using SafeERC20 for IERC20;

    constructor() {}

    event LiFiSetDiamond(address diamond);

    function lifiDiamond() external view returns (address) {
        return LibLiFi.getDiamond();
    }

    function setLiFiDiamond(address _diamond) external onlyOwner {
        LibLiFi.setDiamond(_diamond);
        emit LiFiSetDiamond(_diamond);
    }

    function swap(
        SwapIntegration _route,
        address _approvalAddress,
        address _sourceAsset,
        address _targetAsset,
        uint256 _amount,
        bytes calldata _data
    ) external payable {
        IERC20(_sourceAsset).safeTransferFrom(
            msg.sender,
            address(this),
            _amount
        );

        address sourceAsset = _sourceAsset;
        uint256 sourceAssetInAmount = _amount;

        if (_route == SwapIntegration.LIFI) {
            LibLiFi.execute(
                sourceAsset,
                _approvalAddress,
                sourceAssetInAmount,
                0,
                _data
            );
        }

        uint256 assetOutAmount = IERC20(_targetAsset).balanceOf(address(this));
        IERC20(_targetAsset).safeTransfer(msg.sender, assetOutAmount);
    }

    function swapAndBridge(
        SwapIntegration _route,
        address _sourceAsset,
        address _approvalAddress,
        address _targetAsset,
        uint256 _amount,
        uint256 _targetChain,
        bytes calldata _data
    ) external payable {
        IERC20(_sourceAsset).safeTransferFrom(
            msg.sender,
            address(this),
            _amount
        );

        if (_route == SwapIntegration.LIFI) {
            LibLiFi.execute(
                _sourceAsset,
                _approvalAddress,
                _amount,
                msg.value,
                _data
            );
        }

        uint256 remainingBalance = IERC20(_sourceAsset).balanceOf(address(this));
        if (remainingBalance > 0) {
            IERC20(_sourceAsset).safeTransfer(msg.sender, remainingBalance);
        }
    }

    function lifiBridgeReceiver(
        address _tokenReceived,
        address _sender,
        address _approvalAddress,
        uint256 _sourceAssetInAmount,
        uint256 _value,
        bytes calldata _data
    ) external {
        // Access control?
        //swap and bridge back with the data passed !!

        LibLiFi.execute(
            _tokenReceived,
            _approvalAddress,
            _sourceAssetInAmount,
            _value,
            _data
        );

        uint256 remainingBalance = IERC20(_tokenReceived).balanceOf(
            address(this)
        );
    }

    function emergencyWithdraw(
        address _token
    ) external onlyOwner {
        IERC20(_token).safeTransfer(
            msg.sender,
            IERC20(_token).balanceOf(address(this))
        );
    }
}

contracts/interfaces/IDiamondCut.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamonds: https://eips.ethereum.org/EIPS/eip-2535
/******************************************************************************/

interface IDiamondCut {
    enum FacetCutAction {Add, Replace, Remove}
    // Add=0, Replace=1, Remove=2

    struct FacetCut {
        address facetAddress;
        FacetCutAction action;
        bytes4[] functionSelectors;
    }

    /// @notice Add/replace/remove any number of functions and optionally execute
    ///         a function with delegatecall
    /// @param _diamondCut Contains the facet addresses and function selectors
    /// @param _init The address of the contract or facet to execute _calldata
    /// @param _calldata A function call, including function selector and arguments
    ///                  _calldata is executed with delegatecall on _init
    function diamondCut(
        FacetCut[] calldata _diamondCut,
        address _init,
        bytes calldata _calldata
    ) external;

    event DiamondCut(FacetCut[] _diamondCut, address _init, bytes _calldata);
}

contracts/interfaces/ILiFiPortalSwapRout.content:
// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.0;

interface ILiFiPortalSwapRouter {
    enum SwapIntegration {
        LIFI
    }

    error NotWhitelistedAddress();

    event LiFiExecutionResult(bool success, bytes returnData);

    function swap(
        SwapIntegration _route,
        address _approvalAddress,
        address _sourceAsset,
        address _targetAsset,
        uint256 _amount,
        bytes calldata _data
    ) external payable;

    function swapAndBridge(
        SwapIntegration _route,
        address _sourceAsset,
        address _approvalAddress,
        address _targetAsset,
        uint256 _amount,
        uint256 _targetChain,
        bytes calldata _data
    ) external payable;

    function lifiDiamond() external view returns (address);

    function setLiFiDiamond(address _diamond) external;

    function lifiBridgeReceiver(
        address _tokenReceived,
        address _sender,
        address _approvalAddress,
        uint256 _sourceAssetInAmount,
        uint256 _value,
        bytes calldata _data
    ) external;
}

contracts/libraries/LibLiFi.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamonds: https://eips.ethereum.org/EIPS/eip-2535
/******************************************************************************/
import {IDiamondCut} from "../interfaces/IDiamondCut.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "hardhat/console.sol";
// Remember to add the loupe functions from DiamondLoupeFacet to the diamond.
// The loupe functions are required by the EIP2535 Diamonds standard

error LiFiError(bytes _data);
error UnknownLiFiError();

library LibLiFi {
    using SafeERC20 for IERC20;

    bytes32 constant LIFI_STORAGE_POSITION = keccak256("lifi.portal.tars");

    event LiFiExecutionResult(bool success, bytes returnData);

    struct LiFiConfig {
        address diamond;
    }

    struct LiFiStorage {
        LiFiConfig config;
    }

    function lifiStorage() internal pure returns (LiFiStorage storage ds) {
        bytes32 position = LIFI_STORAGE_POSITION;
        assembly {
            ds.slot := position
        }
    }

    function setDiamond(address _diamond) internal {
        LiFiStorage storage store = lifiStorage();
        store.config.diamond = _diamond;
    }

    function getDiamond() internal view returns (address) {
        return lifiStorage().config.diamond;
    }

    function execute(
        address _sourceAsset,
        address _approvalAddress,
        uint256 _amount,
        uint256 _value,
        bytes calldata _data
    ) internal {
        LiFiStorage storage store = lifiStorage();
        address lifiDiamond = store.config.diamond;

        IERC20(_sourceAsset).safeApprove(_approvalAddress, _amount);

        (bool success, bytes memory returnData) = lifiDiamond.call{
            value: _value
        }(_data);

        if (!success) {
            if (returnData.length == 0) revert UnknownLiFiError();
            assembly {
                revert(add(32, returnData), mload(returnData))
            }
